import React from 'react';
import Pact from 'pact-lang-api';
import QRCode from 'qrcode'
const config = require('../config.json')
const fs = require('fs')

const Context = React.createContext();


const hosts = ["us1", "us2"]
const createAPIHost = (network, chainId) => `https://${network}.testnet.chainweb.com/chainweb/0.0/testnet04/chain/${chainId}/pact`

export class PactStore extends React.Component {

  state = {
    loading: false,
    txObj: {
      0: { pactCode: [], keypairs: [], qrs: [], envData: {}, reqKey: "", pollRes: {} },
      1: { pactCode: [], keypairs: [], qrs: [], envData: {}, reqKey: "", pollRes: {} },
      2: { pactCode: [], keypairs: [], qrs: [], envData: {}, reqKey: "", pollRes: {} },
      3: { pactCode: [], keypairs: [], qrs: [], envData: {}, reqKey: "", pollRes: {} },
      4: { pactCode: [], keypairs: [], qrs: [], envData: {}, reqKey: "", pollRes: {} },
      5: { pactCode: [], keypairs: [], qrs: [], envData: {}, reqKey: "", pollRes: {} },
      6: { pactCode: [], keypairs: [], qrs: [], envData: {}, reqKey: "", pollRes: {} },
      7: { pactCode: [], keypairs: [], qrs: [], envData: {}, reqKey: "", pollRes: {} },
      8: { pactCode: [], keypairs: [], qrs: [], envData: {}, reqKey: "", pollRes: {} },
      9: { pactCode: [], keypairs: [], qrs: [], envData: {}, reqKey: "", pollRes: {} },
    }
  }

  downloadURI = async (uri, name) => {
    var link = document.createElement("a");
    link.download = name;
    link.href = uri;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  }

  generateQR = async text => {
    try {
      const qr = await QRCode.toDataURL(text)
      return qr
    } catch (err) {
      console.error(err)
    }
  }

  printQRs = async (qrs, brand, model, keysChainArr) => {
    const date = new Date().toISOString().slice(0,10)
    qrs.map(async (qr, i) => {
      const keysChain = JSON.parse(keysChainArr[i])
      await this.downloadURI(qr, `${brand}-${model}-${date}-c${keysChain.chainId}-${keysChain.keypair.publicKey.substring(0, 6)}.png`)
    })
    return
  }

  wait = async (timeout) => {
    return new Promise(resolve => {
        setTimeout(resolve, timeout);
    });
  }

  mkSend = async (pactCode, envData, chainId, pubKey, privKey) => {
    const reqKey = await Pact.fetch.send({
      networkId: "testnet04",
      pactCode: pactCode,
      keyPairs: [{...config.covidAdminKeys, clist: {name: "coin.GAS", args: []}}, {publicKey: pubKey, secretKey: privKey, clist: {name: "user.covid.PRINTING-ENTITY", args: [pubKey]}}],
      meta: Pact.lang.mkMeta("covid-admin","0",0.00000001,10000,(Math.round((new Date).getTime()/1000)-15), 28800),
      envData: envData
    }, createAPIHost(hosts[0], chainId));
    return reqKey
  }

  registerTests = async (brand, model, amount, pubKey, privKey) => {
    await this.setState({ loading: true })
    for (let i = 0; i < parseInt(amount); i++) {
      const tx = this.state.txObj;
      const keypair = Pact.crypto.genKeyPair();
      const chainId = keypair.publicKey.replace(/\D/g,'')[0];
      const qrData = JSON.stringify({keypair, chainId: chainId});
      const qr = await this.generateQR(qrData);
      tx[chainId] = {
        pactCode: [...tx[chainId].pactCode, `(user.covid.register-test ${JSON.stringify(pubKey)} ${JSON.stringify(keypair.publicKey)} ${JSON.stringify(i.toString())} ${JSON.stringify(brand)} ${JSON.stringify(model)})`],
        keypairs: [...tx[chainId].keypairs, (keypair)],
        qrs: [...tx[chainId].qrs, qr],
        envData: {...tx[chainId].envData, [i]: {"pred": "keys-all", "keys": [keypair.publicKey]}},
        reqKeys: [],
        pollRes: [],
      }
      await this.setState({ txObj: tx });
    }
    console.log(this.state.txObj);
  }

  registerTestsss = async (brand, model, amount, pubKey, privKey) => {
    await this.setState({ loading: true })
    let pactCode = [];
    let keysChain = [];
    let qrs = []
    let envData = {};
    for (let i = 0; i < parseInt(amount); i++) {
      const keypair = Pact.crypto.genKeyPair();
      //take first digit in public key to be chain id
      const chainId = keypair.publicKey.replace(/\D/g,'')[0];
      const qrData = JSON.stringify({keypair, chainId: chainId})
      keysChain.push(qrData);
      const qr = await this.generateQR(qrData);
      qrs.push(qr)
      pactCode.push(`(user.covid.register-test ${JSON.stringify(pubKey)} ${JSON.stringify(keypair.publicKey)} ${JSON.stringify(i.toString())} ${JSON.stringify(brand)} ${JSON.stringify(model)})`)
      envData[i] = {"pred": "keys-all", "keys": [keypair.publicKey]}
    }
    const reqKey = await Pact.fetch.send({
      networkId: "testnet04",
      pactCode: pactCode.join(" "),
      keyPairs: [{...config.covidAdminKeys, clist: {name: "coin.GAS", args: []}}, {publicKey: pubKey, secretKey: privKey, clist: {name: "user.covid.PRINTING-ENTITY", args: [pubKey]}}],
      meta: Pact.lang.mkMeta("covid-admin","0",0.00000001,10000,(Math.round((new Date).getTime()/1000)-15), 28800),
      envData: envData
    }, createAPIHost(hosts[0], "0"));
    if (reqKey) {
      //check kadena tx status every 10 seconds until we get a response (success or fail)
      var time = 180;
      var pollRes;
      while (time > 0) {
        await this.wait(10000);
        pollRes = await Pact.fetch.poll({requestKeys: [reqKey.requestKeys[0]]}, createAPIHost(hosts[0], "0"));
        if (Object.keys(pollRes).length === 0) {
          console.log('no return poll');
          console.log(pollRes)
          time = time - 10
        } else {
          console.log(pollRes);
          time = 0;
        }
      }
      //tx successful
      if (pollRes[reqKey.requestKeys[0]].result.status === "success"){
        console.log('great success')
        await this.printQRs(qrs, brand, model, keysChain);
        await this.setState({ loading: false })
        alert("Your test were successfully registered on Kadena mainnet, and QR code labels downloaded")
        window.location.reload();
      //tx unsuccessful
      } else {
        console.log(pollRes)
        await this.setState({ loading: false })
        alert("There was a problem processing your transaction on Kadena mainnet. Your tests were not registered")
        window.location.reload();
      }
    } else {
      //blockchain call had formatting issues
      await this.setState({ loading: false })
      alert("There was a problem processing your transaction on Kadena mainnet. Your tests were not registered")
      window.location.reload();
    }
  }

  render() {
    return (
      <Context.Provider
        value={{
          ...this.state,
          registerTests: this.registerTests
        }}
      >
        {this.props.children}
      </Context.Provider>
    );
  }

}

export default Context;
