import React from 'react';
import Pact from 'pact-lang-api';
const config = require('../config.json')

const Context = React.createContext();


const hosts = ["us1", "us2"]
const createAPIHost = (network, chainId) => `https://${network}.testnet.chainweb.com/chainweb/0.0/testnet04/chain/${chainId}/pact`

export class PactStore extends React.Component {

  state = {
    loading: false
  }

  wait = async (timeout) => {
    return new Promise(resolve => {
        setTimeout(resolve, timeout);
    });
  }

  registerTests = async (brand, model, amount, pubKey, privKey) => {
    let pactCode = [];
    let keypairs = [];
    for (let i = 0; i < parseInt(amount) - 1 ; i++) {
      const keypair = Pact.crypto.genKeyPair();
      keypairs.push(keypair);
      pactCode.push(`(user.covid.register-test ${JSON.stringify(pubKey)} ${JSON.stringify(keypair.publicKey)} ${JSON.stringify(brand)} ${JSON.stringify(model)})`)
    }
    const reqKey = await Pact.fetch.send({
    networkId: "testnet04",
    pactCode:`(user.covid.administer-test ${JSON.stringify(pubKey)} ${JSON.stringify(ageGroup)} ${JSON.stringify(gender)} ${JSON.stringify(hash(patientJSON))})`,
    keyPairs: [{...config.covidAdminKeys, clist: {name: "coin.GAS", args: []}}, {publicKey: pubKey, secretKey: privKey, clist: {name: "user.covid.REGISTERED-TEST", args: [pubKey]}}],
    meta: Pact.lang.mkMeta("covid-admin","0",0.00000001,10000,(Math.round((new Date).getTime()/1000)-15), 28800)}, createAPIHost(hosts[0], chainId));
    if (reqKey) {
      //check kadena tx status every 10 seconds until we get a response (success or fail)
      var time = 180;
      var pollRes;
      while (time > 0) {
        await this.wait(10000);
        pollRes = await Pact.fetch.poll({requestKeys: [reqKey.requestKeys[0]]}, createAPIHost(hosts[0], "0"));
        if (Object.keys(pollRes).length === 0) {
          console.log('no return poll');
          console.log(pollRes)
          time = time - 10
        } else {
          console.log(pollRes);
          time = 0;
        }
      }
      //tx successful
      if (pollRes[reqKey.requestKeys[0]].result.status === "success"){
        console.log('great success')
        //doc registered on blockchain, now add to db
        // await this.setState({loading: false, txStatus: 'success', txData: pollRes[reqKey.requestKeys[0]]})
        // alert("The test was successfuly registered on Kadena mainnet")
        //SHOW A MESSAGE OF SUCCESS
        // window.location.reload();
      //tx unsuccessful
      } else {
        // await this.setState({loading: false, txStatus: 'failure', txData: pollRes[reqKey.requestKeys[0]]})
        // alert("there was a problem processing your transaction on Kadena mainnet")
        // window.location.reload();
      }
    } else {
      //blockchain call had formatting issues
      // await this.setState({loading: false, txStatus: 'failure', txData: pollRes[reqKey.requestKeys[0]]})
      // alert("there was a problem processing your transaction on Kadena mainnet")
      // window.location.reload();
    }
  }

  render() {
    return (
      <Context.Provider
        value={{
          ...this.state,
          registerTests: this.registerTests
        }}
      >
        {this.props.children}
      </Context.Provider>
    );
  }

}

export default Context;
