import React from 'react';
import Pact from 'pact-lang-api';
import QRCode from 'qrcode'
const config = require('../config.json')
const fs = require('fs')

const Context = React.createContext();


const hosts = ["us1", "us2"]
const createAPIHost = (network, chainId) => `https://${network}.testnet.chainweb.com/chainweb/0.0/testnet04/chain/${chainId}/pact`

export class PactStore extends React.Component {

  state = {
    loading: false
  }

  downloadURI = (uri, name) => {
    var link = document.createElement("a");
    link.download = name;
    link.href = uri;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  }

  generateQR = async text => {
    try {
      const qr = await QRCode.toDataURL(text)
      return qr
    } catch (err) {
      console.error(err)
    }
  }

  wait = async (timeout) => {
    return new Promise(resolve => {
        setTimeout(resolve, timeout);
    });
  }

  registerTests = async (brand, model, amount, pubKey, privKey) => {
    await this.setState({ loading: true })
    let pactCode = [];
    let keypairs = [];
    let qrs = []
    let envData = {};
    // envData: {"doc-ks": {"pred": "keys-all", "keys": [docKP.publicKey]}},
    for (let i = 0; i < parseInt(amount); i++) {
      const keypair = Pact.crypto.genKeyPair();
      keypairs.push(keypair);
      const qr = await this.generateQR(JSON.stringify({...keypair, chainId: "0"}));
      qrs.push(qr)
      pactCode.push(`(user.covid.register-test ${JSON.stringify(pubKey)} ${JSON.stringify(keypair.publicKey)} ${JSON.stringify(i.toString())} ${JSON.stringify(brand)} ${JSON.stringify(model)})`)
      envData[i] = {"pred": "keys-all", "keys": [keypair.publicKey]}
    }
    console.log(keypairs)
    console.log(pactCode)
    console.log(envData)
    const reqKey = await Pact.fetch.send({
      networkId: "testnet04",
      pactCode: pactCode.join(" "),
      keyPairs: [{...config.covidAdminKeys, clist: {name: "coin.GAS", args: []}}, {publicKey: pubKey, secretKey: privKey, clist: {name: "user.covid.PRINTING-ENTITY", args: [pubKey]}}],
      meta: Pact.lang.mkMeta("covid-admin","0",0.00000001,10000,(Math.round((new Date).getTime()/1000)-15), 28800),
      envData: envData
    }, createAPIHost(hosts[0], "0"));
    if (reqKey) {
      //check kadena tx status every 10 seconds until we get a response (success or fail)
      var time = 180;
      var pollRes;
      while (time > 0) {
        await this.wait(10000);
        pollRes = await Pact.fetch.poll({requestKeys: [reqKey.requestKeys[0]]}, createAPIHost(hosts[0], "0"));
        if (Object.keys(pollRes).length === 0) {
          console.log('no return poll');
          console.log(pollRes)
          time = time - 10
        } else {
          console.log(pollRes);
          time = 0;
        }
      }
      //tx successful
      if (pollRes[reqKey.requestKeys[0]].result.status === "success"){
        console.log('great success')
        const date = new Date().toISOString().slice(0,10)
        qrs.map((qr, i) => {
          this.downloadURI(qr, `${date}-c0-${keypairs[i]["publicKey"].substring(0, 6)}.png`)
        })
        await this.setState({ loading: false })
        //doc registered on blockchain, now add to db
        // await this.setState({loading: false, txStatus: 'success', txData: pollRes[reqKey.requestKeys[0]]})
        // alert("The test was successfuly registered on Kadena mainnet")
        //SHOW A MESSAGE OF SUCCESS
        // window.location.reload();
      //tx unsuccessful
      } else {
        console.log(pollRes)
        await this.setState({ loading: false })
        alert("There was a problem processing your transaction on Kadena mainnet. Your tests were not registered")
        window.location.reload();
      }
    } else {
      //blockchain call had formatting issues
      await this.setState({ loading: false })
      alert("There was a problem processing your transaction on Kadena mainnet. Your tests were not registered")
      window.location.reload();
    }
  }

  render() {
    return (
      <Context.Provider
        value={{
          ...this.state,
          registerTests: this.registerTests
        }}
      >
        {this.props.children}
      </Context.Provider>
    );
  }

}

export default Context;
