import React, { useState, useEffect, useContext } from 'react';
import '../App.css';
import { Button, Grid, Input, Icon, Form, List,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';
import PactContext from "../contexts/PactContext";

function Home() {

  const pactContext = useContext(PactContext);

  const [name, setName] = useState(null);
  const [model, setModel] = useState(null);
  const [num, setNum] = useState(null);
  const [pubKey, setPubKey] = useState(null);
  const [privKey, setPrivKey] = useState(null);
  const [error, setError] = useState(false);
  const [errMsg, setErrMsg] = useState("");

  function toKeyLoad(){
   try {
     var fileToLoad = document.getElementById("to-pub-file").files[0];
     if (fileToLoad.name.substr(fileToLoad.name.length - 4) !== ".kda" || fileToLoad.name.includes("public")) {
       alert(`file must be a .kda private key file`)
     }
     var fileReader = new FileReader();
     fileReader.onload = function(fileLoadedEvent){
         var textFromFileLoaded = fileLoadedEvent.target.result;
         var keys = textFromFileLoaded.split("\n");
         const pub = keys[0].replace("public: ", "");
         const priv = keys[1].replace("secret: ", "")
         checkKey(pub);
         checkKey(priv);
         setPubKey(pub);
         setPrivKey(priv);
     };
     fileReader.readAsText(fileToLoad, "UTF-8");
   } catch (err) {
     console.log(err)
     alert(`file must be a .kda private key file`)
   }
 }

 const is_hexadecimal = (str) => {
   const regexp = /^[0-9a-fA-F]+$/;
   if (regexp.test(str)) return true;
   else return false;
  }

 const checkKey = (key) => {
   console.log(key)
     if (key.length !== 64) {
       setErrMsg("Key length is invalid")
       return true
     } else if (!is_hexadecimal(key)){
       setErrMsg("Key format is invalid")
       return true
     } else {
       return false
     }
 }

 const checkText = (text) => {
   if (text === "") {
     setErrMsg("Please to not leave field empty")
     return true
   } else {
     return false
   }
 }

 const checkNum = (num) => {
    if(num.match(/^-{0,1}\d+$/)){
      return false
    } else {
      // setError(true)
      setErrMsg("Please enter an integer for the amount of labels")
      return true
      // setErrTrig("num")
    }
  }

  const checkError = () => {
    if (!checkNum(num) && !checkText(name) && !checkText(model) && !checkKey(pubKey) && !checkKey(privKey)) {
      setError(false);
      setErrMsg("");
      return false
    } else {
      setError(true);
      return true
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={require("../kadena.png")} style={{height:100, marginBottom: 10}}/>
        <p>
          Covid-19 Test QR Generator
        </p>
        <Form>
          <Form.Field  style={{marginTop: "0px", marginBottom: 10, textAlign: "left"}} >
            <label style={{color: "#18A33C" }}>1. Enter Test Manufacturer Name
              <Popup
                trigger={
                  <Icon name='help circle' style={{"marginLeft": "2px"}}/>
                }
                position='top center'
              >
              <Popup.Header>What is the Test Manufacturer Name? </Popup.Header>
              <Popup.Content>This is the name of the company to that produces or brands the test</Popup.Content>
              </Popup>
            </label>
            <Form.Input
              icon='building'
              iconPosition='left'
              placeholder='Manufacturer Name'
              value={name}
              onChange={(e) => {
                checkText(e.target.value)
                setName(e.target.value)
              }}
            />
          </Form.Field>
          <Form.Field  style={{marginTop: "0px", marginBottom: 10, textAlign: "left"}} >
            <label style={{color: "#18A33C" }}>2. Enter Test Model Name
              <Popup
                trigger={
                  <Icon name='help circle' style={{"marginLeft": "2px"}}/>
                }
                position='top center'
              >
              <Popup.Header>What is the Test Model Name? </Popup.Header>
              <Popup.Content>This is the name that identifies this type of test within the manufacturer or brand</Popup.Content>
              </Popup>
            </label>
            <Form.Input
              icon='pencil alternate'
              iconPosition='left'
              placeholder='Model Name'
              value={model}
              onChange={(e) => {
                checkText(e.target.value)
                setModel(e.target.value)
              }}
            />
          </Form.Field>
          <Form.Field  style={{marginTop: "0px", marginBottom: 10, textAlign: "left"}} >
            <label style={{color: "#18A33C" }}>3. Amount of Test Labels to Print
              <Popup
                trigger={
                  <Icon name='help circle' style={{"marginLeft": "2px"}}/>
                }
                position='top center'
              >
              <Popup.Header>What is the Test Model Name? </Popup.Header>
              <Popup.Content>This is the name that identifies this type of test within the manufacturer or brand</Popup.Content>
              </Popup>
            </label>
            <Form.Input
              icon='hashtag'
              iconPosition='left'
              placeholder='Label Amount'
              value={num}
              onChange={(e) => {
                checkNum(e.target.value)
                setNum(e.target.value)
              }}
            />
          </Form.Field>
          <Form.Field  style={{marginTop: "0px", marginBottom: 10, textAlign: "left"}} >
            <label style={{color: "#18A33C" }}>4. Sign Test Labels on Blockchain
              <Popup
                trigger={
                  <Icon name='help circle' style={{"marginLeft": "2px"}}/>
                }
                position='top center'
              >
              <Popup.Header>What is Signing? </Popup.Header>
              <Popup.Content>Enter your public and private key provided by Kadena to sign and generate QR codes</Popup.Content>
              </Popup>
            </label>
            <Input
              style={{marginBottom: 5}}
              placeholder='Public Key'
              icon="key"
              iconPosition="left"
              value={pubKey}
              onChange={(e) => {
                checkKey(e.target.value)
                setPubKey(e.target.value)
              }}
            />
            <Form.Input
              style={{marginBottom: 0}}
              placeholder='Private Key'
              icon="lock"
              iconPosition="left"
              value={privKey}
              onChange={(e) => {
                checkKey(e.target.value)
                setPrivKey(e.target.value)
              }}
            />
            <div style={{marginBottom: 20}}>
              <input
                  id="to-pub-file"
                  type="file"
                  onChange={(e) => toKeyLoad()}/>
            </div>

          </Form.Field>
          {error ?
            <div style={{marginBottom: 20}}>
              <Message color='red'>
                <Message.Header style={{marginBottom: 5}}>
                  Input Error
                </Message.Header>
                {errMsg}
              </Message>
            </div>
          : <div></div>}
          <Form.Field  style={{marginTop: "0px", marginBottom: 10, textAlign: "left"}} >
            <Button
              disabled={checkError}
              style={{
              backgroundColor: "#18A33C",
              color: "white",
              width: 340,
              }}
              onClick={() => pactContext.relAll()}
            >
              Sign and Generate QR Codes
            </Button>
          </Form.Field>
        </Form>

      </header>
    </div>
  );
}

export default Home;
