import React, { useState, useEffect, useContext } from 'react';
import '../App.css';
import { Button, Grid, Input, Icon, Form, List,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';
import PactContext from "../contexts/PactContext";

function Home() {

  const pactContext = useContext(PactContext);

  const [name, setName] = useState("");
  const [model, setModel] = useState("");
  const [num, setNum] = useState("");
  const [pubKey, setPubKey] = useState("");
  const [privKey, setPrivKey] = useState("");
  const [error, setError] = useState(false);

  function toPubLoad(isPub){
   try {
     var fileToLoad = document.getElementById("to-pub-file").files[0];
     if (fileToLoad.name.substr(fileToLoad.name.length - 4) !== ".kda" || fileToLoad.name.includes((isPub ? "private" : "public"))) {
       alert(`file must be a .kda ${(!isPub ? "private" : "public")} key file`)
       // document.getElementById("to-pub-file").files = ""
     }
     var fileReader = new FileReader();
     fileReader.onload = function(fileLoadedEvent){
         var textFromFileLoaded = fileLoadedEvent.target.result;
         // console.log(textFromFileLoaded)
         if (isPub) {
           setPubKey(textFromFileLoaded.replace("public: ", ""))
         } else {
           var keys = textFromFileLoaded.split("\n");
           setPubKey(keys[0].replace("public: ", ""));
           setPrivKey(keys[1].replace("secret: ", ""));
         }
     };
     fileReader.readAsText(fileToLoad, "UTF-8");
   } catch (err) {
     console.log(err)
     alert(`file must be a .kda ${(!isPub ? "private" : "public")} key file`)
   }
 }

 const is_hexadecimal = (str) => {
   const regexp = /^[0-9a-fA-F]+$/;
   if (regexp.test(str)) return true;
   else return false;
  }

 const checkKey = (key) => {
     if (key.length !== 64) {
         return false
     } else if (!is_hexadecimal(key)){
         return false
     }
     return true;
 }

  return (
    <div className="App">
      <header className="App-header">
        <img src={require("../kadena.png")} style={{height:100, marginBottom: 10}}/>
        <p>
          Covid-19 Test QR Generator
        </p>
        <Form>
          <Form.Field  style={{marginTop: "0px", marginBottom: 10, textAlign: "left"}} >
            <label style={{color: "#18A33C" }}>1. Enter Test Manufacturer Name
              <Popup
                trigger={
                  <Icon name='help circle' style={{"marginLeft": "2px"}}/>
                }
                position='top center'
              >
              <Popup.Header>What is the Test Manufacturer Name? </Popup.Header>
              <Popup.Content>This is the name of the company to that produces or brands the test</Popup.Content>
              </Popup>
            </label>
            <Form.Input
              icon='building'
              iconPosition='left'
              placeholder='Manufacturer Name'
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </Form.Field>
          <Form.Field  style={{marginTop: "0px", marginBottom: 10, textAlign: "left"}} >
            <label style={{color: "#18A33C" }}>2. Enter Test Model Name
              <Popup
                trigger={
                  <Icon name='help circle' style={{"marginLeft": "2px"}}/>
                }
                position='top center'
              >
              <Popup.Header>What is the Test Model Name? </Popup.Header>
              <Popup.Content>This is the name that identifies this type of test within the manufacturer or brand</Popup.Content>
              </Popup>
            </label>
            <Form.Input
              icon='pencil alternate'
              iconPosition='left'
              placeholder='Model Name'
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </Form.Field>
          <Form.Field  style={{marginTop: "0px", marginBottom: 10, textAlign: "left"}} >
            <label style={{color: "#18A33C" }}>3. Amount of Labels to Print
              <Popup
                trigger={
                  <Icon name='help circle' style={{"marginLeft": "2px"}}/>
                }
                position='top center'
              >
              <Popup.Header>What is the Test Model Name? </Popup.Header>
              <Popup.Content>This is the name that identifies this type of test within the manufacturer or brand</Popup.Content>
              </Popup>
            </label>
            <Form.Input
              icon='hashtag'
              iconPosition='left'
              placeholder='Label Amount'
              value={num}
              onChange={(e) => setNum(e.target.value)}
            />
          </Form.Field>
          <Form.Field  style={{marginTop: "0px", marginBottom: 10, textAlign: "left"}} >
            <label style={{color: "#18A33C" }}>3. Sign Test Labels on Blockchain
              <Popup
                trigger={
                  <Icon name='help circle' style={{"marginLeft": "2px"}}/>
                }
                position='top center'
              >
              <Popup.Header>What is Signing? </Popup.Header>
              <Popup.Content>Enter your public and private key provided by Kadena to sign and generate QR codes</Popup.Content>
              </Popup>
            </label>
            <Input
              style={{marginBottom: 5}}
              placeholder='Public Key'
              icon="key"
              iconPosition="left"
              value={pubKey}
              onChange={(e) => setPubKey(e.target.value)}
            />
            <Form.Input
              style={{marginBottom: 0}}
              placeholder='Private Key'
              icon="lock"
              iconPosition="left"
              value={privKey}
              onChange={(e) => setPrivKey(e.target.value)}
            />
            <div style={{marginBottom: 20}}>
              <input
                  id="to-pub-file"
                  type="file"
                  onChange={(e) => toPubLoad(false)}/>
            </div>
            <Button
              disabled={name === "" || model === "" || checkKey(pubKey) || checkKey(privKey)}
              style={{
              backgroundColor: "#18A33C",
              color: "white",
              width: 340,
              }}
              onClick={() => pactContext.relAll()}
            >
              Sign and Generate QR Codes
            </Button>
          </Form.Field>
          {error ?
            <div>
            <Form.Field  style={{marginTop: "0px", marginBottom: 10, textAlign: "left"}} >
              error
            </Form.Field>
            </div>
          : <div></div>}
        </Form>
      </header>
    </div>
  );
}

export default Home;

