import React, { useState, useEffect, useContext, Fragment, useRef } from 'react';
import { Button, Grid, Input, Icon, Form, List, Label,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';
import QrReader from 'react-qr-reader'

const QRScan = () => {

  const [showQR, setshowQR] = useState(true);
  const [serial, setSerial] = useState("");
  const [loading, setLoading] = useState(false);

  return (
    <div style={{marginTop: 20}}>

      {showQR ?
        <div>
          <QrReader
            onError={(e) => console.log(e)}
            onScan={(data) => {
              if (data) {
                setSerial(data);
                setshowQR(false);
              }
            }}
          />
          <Button
              style={{
                backgroundColor: "#18A33C",
                color: "white",
                width: 160,
                marginTop: 5
                }}
              onClick={() => setshowQR(false)}
            >
            Close and Type
          </Button>
        </div>
        :
        <Form.Field  style={{width:"240px", margin: "0 auto", marginTop: "10px", marginBottom: 0}} >
          <label style={{color: "#18A33C", fontWeight: 'bold', marginBottom: 20}}>Test Serial Number
            <Popup
              trigger={
                <Icon name='help circle' style={{"marginLeft": "2px"}}/>
              }
              position='top center'
            >
              <Popup.Header>Test Serial Number</Popup.Header>
              <Popup.Content>Scan QR code on test or enter number manually</Popup.Content>
            </Popup>
          </label>
          <div>
            <Input
              style={{width:"240px", marginBottom: 3, marginTop: 5}}
              icon='hashtag'
              iconPosition='left'
              placeholder='Serial Number'
              value={serial}
              onChange={(e) => setSerial(e.target.value)}
            />
          </div>
          <Button
              style={{
                backgroundColor: "#18A33C",
                color: "white",
                width: 240,
                marginTop: 5
                }}
              onClick={() => console.log('clicl')}
            >
            Submit
          </Button>
        </Form.Field>
      }

    </div>
  )

}

export default QRScan;
