import React from 'react';
import Pact from 'pact-lang-api';
var hash = require('object-hash');
const config = require('../config.json')

const Context = React.createContext();

const hosts = ["us1", "us2"]
const createAPIHost = (network, chainId) => `https://${network}.testnet.chainweb.com/chainweb/0.0/testnet04/chain/${chainId}/pact`

export class PactCallStore extends React.Component {

  state = {
    testInfo: {},
    txData: {},
    loading: false,
    txStatus: ""
  }

  wait = async (timeout) => {
    return new Promise(resolve => {
        setTimeout(resolve, timeout);
    });
  }

  getTest = async (pubKey, chainId) => {
    await this.setState({ loading: true })
    const res = await Pact.fetch.local({
        networkId: "testnet04",
        // pactCode:`(user.covid.get-record ${JSON.stringify(pubKey)})`,
        pactCode: `(user.covid.get-record ${JSON.stringify(pubKey)})`,
        keyPairs: [{...config.covidAdminKeys, clist: {name: "coin.GAS", args: []}}],
        meta: Pact.lang.mkMeta("dummy-local","0",0.00000001,10000,(Math.round((new Date).getTime()/1000)-15), 28800)}, createAPIHost(hosts[0], "0"))
    if (res.result.status === "success") {
      await this.setState({ testInfo: res.result.data })
      await this.setState({ loading: false })
      return res.result.data;
    } else {
      await this.setState({ testInfo: "failure" })
      await this.setState({ loading: false })
      return "failure"
    }
  }

  administerTest = async (pubKey, privKey, chainId, ageGroup, gender, name, surname, dob, id) => {
    await this.setState({ loading: true })
    const patientJSON = {
      name: name,
      surname: surname,
      dob: dob,
      id: id
    }
    const reqKey = await Pact.fetch.send({
        networkId: "testnet04",
        pactCode:`(user.covid.administer-test ${JSON.stringify(pubKey)} ${JSON.stringify(ageGroup)} ${JSON.stringify(gender)} ${JSON.stringify(hash(patientJSON))})`,
        keyPairs: [{...config.covidAdminKeys, clist: {name: "coin.GAS", args: []}}, {publicKey: pubKey, secretKey: privKey, clist: {name: "user.covid.REGISTERED-TEST", args: [pubKey]}}],
        meta: Pact.lang.mkMeta("covid-admin","0",0.00000001,10000,(Math.round((new Date).getTime()/1000)-15), 28800)}, createAPIHost(hosts[0], chainId));
        if (reqKey) {
          //check kadena tx status every 10 seconds until we get a response (success or fail)
          var time = 180;
          var pollRes;
          while (time > 0) {
            await this.wait(10000);
            pollRes = await Pact.fetch.poll({requestKeys: [reqKey.requestKeys[0]]}, createAPIHost(hosts[0], "0"));
            if (Object.keys(pollRes).length === 0) {
              console.log('no return poll');
              console.log(pollRes)
              time = time - 10
            } else {
              console.log(pollRes);
              time = 0;
            }
          }
          //tx successful
          if (pollRes[reqKey.requestKeys[0]].result.status === "success"){
            //doc registered on blockchain, now add to db
            await this.setState({loading: false, txStatus: 'success', txData: pollRes[reqKey.requestKeys[0]]})
            // alert("The test was successfuly registered on Kadena mainnet")
            //SHOW A MESSAGE OF SUCCESS
            // window.location.reload();
          //tx unsuccessful
          } else {
            await this.setState({loading: false, txStatus: 'failure', txData: pollRes[reqKey.requestKeys[0]]})
            // alert("there was a problem processing your transaction on Kadena mainnet")
            // window.location.reload();
          }
        } else {
          //blockchain call had formatting issues
          await this.setState({loading: false, txStatus: 'failure', txData: pollRes[reqKey.requestKeys[0]]})
          // alert("there was a problem processing your transaction on Kadena mainnet")
          // window.location.reload();
        }
  }

  endTest = async (pubKey, privKey, chainId, result) => {
    await this.setState({ loading: true })
    const reqKey = await Pact.fetch.send({
        networkId: "testnet04",
        pactCode:`(user.covid.end-test ${JSON.stringify(pubKey)} ${JSON.stringify(result)})`,
        keyPairs: [{...config.covidAdminKeys, clist: {name: "coin.GAS", args: []}}, {publicKey: pubKey, secretKey: privKey, clist: {name: "user.covid.REGISTERED-TEST", args: [pubKey]}}],
        meta: Pact.lang.mkMeta("covid-admin","0",0.00000001,10000,(Math.round((new Date).getTime()/1000)-15), 28800)}, createAPIHost(hosts[0], chainId));
        if (reqKey) {
          //check kadena tx status every 10 seconds until we get a response (success or fail)
          var time = 180;
          var pollRes;
          while (time > 0) {
            await this.wait(10000);
            pollRes = await Pact.fetch.poll({requestKeys: [reqKey.requestKeys[0]]}, createAPIHost(hosts[0], "0"));
            if (Object.keys(pollRes).length === 0) {
              console.log('no return poll');
              console.log(pollRes)
              time = time - 10
            } else {
              console.log(pollRes);
              time = 0;
            }
          }
          //tx successful
          if (pollRes[reqKey.requestKeys[0]].result.status === "success"){
            //doc registered on blockchain, now add to db
            await this.setState({loading: false, txStatus: 'success', txData: pollRes[reqKey.requestKeys[0]]})
          //tx unsuccessful
          } else {
            await this.setState({loading: false, txStatus: 'failure', txData: pollRes[reqKey.requestKeys[0]]})
          }
        } else {
          //blockchain call had formatting issues
          await this.setState({loading: false, txStatus: 'failure', txData: pollRes[reqKey.requestKeys[0]]})
        }
  }


  render() {
    return (
      <Context.Provider
        value={{
          ...this.state,
          getTest: this.getTest,
          administerTest: this.administerTest,
          endTest: this.endTest
        }}
      >
        {this.props.children}
      </Context.Provider>
    );
  }

}

export default Context;
