import React, { useState, useEffect, useContext } from 'react';
import { Button, Grid, Input, Icon, Form, List, Table, Image,
   Modal, Header, Message, Popup, Select, Radio, Segment,
   Tab, TextArea, Loader } from 'semantic-ui-react';
import AuthContext from "../contexts/AuthContext.js";
import PactContext from "../contexts/PactContext.js";

const History = () => {

  const authContext = useContext(AuthContext);

  useEffect(() => {
	      async function fetchTxs() {
	         await authContext.fetchDocTxs(authContext.docUuid);
	          }
	      fetchTxs()
	    }, []);

  const renderRows = () => {
    const cmps = []
    authContext.docTxs.map((r, i) => {
      const color = (r.kuuid_end ? "green" : "red")
      cmps.push(
      	<div>
      	<Message  color={color}  style={{margin: 0}}>
      	      <Grid columns={3} divided>
      	        <Grid.Column>
      	          {r.serial}
      	        </Grid.Column>
      	        <Grid.Column>
      	          <a href={`https://explorer.chainweb.com/testnet/chain/0/tx/${r.kuuid_init}`}>{`${r.kuuid_init.substring(0,3)}...${r.kuuid_init.substring(r.kuuid_init.length - 3, r.kuuid_init.length)}`}</a>
      			    </Grid.Column>
      			    <Grid.Column>
      			     {(r.kuuid_end ? <a href={`https://explorer.chainweb.com/testnet/chain/0/tx/${r.kuuid_end}`}>{`${r.kuuid_end.substring(0,3)}...${r.kuuid_end.substring(r.kuuid_end.length - 3, r.kuuid_end.length)}`}</a> : <div>no result</div>)}
      			  </Grid.Column>
      			  </Grid>
      			  </Message>
      	</div>
      )
    })
    return cmps
  }

  return (
    <div>
      <Message color="blue" style={{margin: 0}}>
        <Grid columns={3} divided>
          <Grid.Column>
            <Header as='h4'>Serial #</Header>
          </Grid.Column>
          <Grid.Column>
            <Header as='h4'>Init Tx</Header>
          </Grid.Column>
          <Grid.Column>
            <Header as='h4'>End Tx</Header>
          </Grid.Column>
        </Grid>
      </Message>
      {renderRows()}

    </div>
  )

}

export default History;
