import React, { useState, useEffect, useContext, Fragment, useRef } from 'react';
import { Button, Grid, Input, Icon, Form, List, Label,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';
import QrReader from 'react-qr-reader'
// import Camera from 'react-html5-camera-photo';
import { Camera } from 'react-cam';
// import Webcam from "react-webcam";
import PactContext from '../contexts/PactContext'
import AuthContext from '../contexts/AuthContext'
import DocInfo from './DocInfo';
import History from './History';
import Scan from './Scan';

var hash = require('object-hash');


const Tabs = () => {

  const pactContext = useContext(PactContext);

  const authContext = useContext(AuthContext);

  useEffect(() => {
    async function fetchDoc() {
      await authContext.fetchDocInfo(authContext.docUuid);
    }
    fetchDoc()
  }, []);


  const [showQR, setshowQR] = useState(false);
  const [serial, setSerial] = useState("");
  const [age, setAge] = useState("");
  const [sex, setSex] = useState("");
  const [res, setRes] = useState("");
  const [imgHash, setImgHash] = useState("");
  const [imgBlob, setImgBlob] = useState("");
  const [canClick, setCanClick] = useState(false);
  const [showCam, setShowCam] = useState(false);

  const ages = [
      { key: '0', value: '0-10', text: '0-10' },
      { key: '1', value: '11-20', text: '11-20' },
      { key: '2', value: '21-30', text: '21-30' },
      { key: '3', value: '41-50', text: '41-50' },
      { key: '4', value: '51-60', text: '51-60' },
      { key: '5', value: '61-70', text: '61-70' },
      { key: '6', value: '71-80', text: '71-80' },
      { key: '7', value: '81-90', text: '81-90' },
      { key: '8', value: '91-100', text: '91-100' },
    ]


  function capture(imgSrc) {
    setImgBlob(imgSrc);
    setImgHash(hash(imgSrc))
    setCanClick(true)
    setShowCam(false);
  }
  const cam = useRef(null);

  const camera = () => {
    return (
      <Message color="blue">
        <Fragment>
          <Camera
            showFocus={true}
            front={false}
            capture={capture}
            ref={cam}
            width="80%"
            height="auto"
            focusWidth="80%"
            focusHeight="60%"
            btnColor="white"
          />
          <Button
            onClick={img => cam.current.capture(img)}
            style={{
              backgroundColor: "#18A33C",
              color: "white",
              width: 140,
              marginTop: 10
              }}
          >
            Take Picture
          </Button>
        </Fragment>
      </Message>
    )
  }

  const panes = [
    { menuItem: { key: 'init', icon: 'qrcode', content: 'Scan' }, render: () => <Tab.Pane>
          <div>
            <Message>
              <img src={require("../assets/kadena-big.png")} style={{height:40, marginTop: 0}}/>
              <Message.Header style={{marginBottom: 10}}>
                Scan QR Code
              </Message.Header>
                <Scan />
            </Message>
          </div>
        </Tab.Pane> },

        { menuItem: { key: 'info', icon: 'exchange', content: 'History' }, render: () =>
        <Tab.Pane>
          <div>
            <Message>
              <img src={require("../assets/kadena-big.png")} style={{height:40, marginTop: 0}}/>
              <Message.Header style={{marginBottom: 10}}>
                Blockchain Transaction History
              </Message.Header>
              <History/>
            </Message>
          </div>
        </Tab.Pane> },

        { menuItem: { key: 'info', icon: 'user md', content: 'Info' }, render: () =>
        <Tab.Pane>
          <div>
            <Message>
              <Message.Header style={{marginBottom: 10}}>
                Doctor Information
              </Message.Header>
              <DocInfo/>
            </Message>
          </div>
        </Tab.Pane> },

  ]

  return (
    <div>
      <Tab panes={panes} menu={{color:'blue', attached: 'top' }}/>
    </div>
  )

}

export default Tabs;


