      }
    }

  }

  const handleQRError = err => {
    console.error(err)
  }

  return (
    <Segment style={{ margin: 10 }}>
    <div style={{marginTop: 10}}>
    {(scanType === "" ?
      <div>
        <Header as='h1'  textAlign='center' style={{color: "#054F9E", marginBottom: 20, marginTop: 0 }}>
          {(scanType === "" ? "Scan QR Code" : "")}
        </Header>
      </div>
     : <div></div>)}
    {(loading ? <Loader active inline /> :
      (scanType === ""
        ?
              <div>
                <Message color="blue">
                  <QrReader
