import React, { useState, useEffect, useContext } from 'react';
import { Button, Grid, Input, Icon, Form, List,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';

import AuthContext from "../contexts/AuthContext"


const SignUp = () => {

  const authContext = useContext(AuthContext);

  const [ak, setAk] = useState("");
  const [name, setName] = useState("");
  const [hospital, setHospital] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confPassword, setConfPassword] = useState("");

  return (
    <Form>
      <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "20px", marginBottom: 0, textAlign: "left"}} >
        <label style={{color: "#054f9e" }}>Enter Your Access Key
          <Popup
            trigger={
              <Icon name='help circle' style={{"marginLeft": "2px"}}/>
            }
            position='top center'
          >
          <Popup.Header>What is an Access Key? </Popup.Header>
          <Popup.Content>This is an access key provided to you by your organization</Popup.Content>
          </Popup>
        </label>
          <Form.Input
            icon='key'
            iconPosition='left'
            placeholder='Access Key'
            value={ak}
            onChange={(e) => setAk(e.target.value)}
          />
      </Form.Field>
      <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "20px", marginBottom: 0, textAlign: "left"}} >
        <label style={{color: "#054f9e" }}>Enter Your Full Name
          <Popup
            trigger={
              <Icon name='help circle' style={{"marginLeft": "2px"}}/>
            }
            position='top center'
          >
          <Popup.Header>What is this Name? </Popup.Header>
          <Popup.Content>This is your full name and surname</Popup.Content>
          </Popup>
        </label>
          <Form.Input
            icon='user md'
            iconPosition='left'
            placeholder='Name'
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
      </Form.Field>
      <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "20px", marginBottom: 0, textAlign: "left"}} >
        <label style={{color: "#054f9e" }}>Enter Your Residency Hospital or Organization
          <Popup
            trigger={
              <Icon name='help circle' style={{"marginLeft": "2px"}}/>
            }
            position='top center'
          >
          <Popup.Header>What is this? </Popup.Header>
          <Popup.Content>This is where you work or are registered</Popup.Content>
          </Popup>
        </label>
          <Form.Input
            icon='hospital'
            iconPosition='left'
            placeholder='Hospital'
            value={hospital}
            onChange={(e) => setHospital(e.target.value)}
          />
      </Form.Field>
      <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "20px", marginBottom: 0, textAlign: "left"}} >
        <label style={{color: "#054f9e" }}>Enter Your Username and Password
          <Popup
            trigger={
              <Icon name='help circle' style={{"marginLeft": "2px"}}/>
            }
            position='top center'
          >
          <Popup.Header>What is Username and Password? </Popup.Header>
          <Popup.Content>You will set a username and confirm a password that you will use to log into the system from now on</Popup.Content>
          </Popup>
        </label>
          <Form.Input
            icon='user'
            iconPosition='left'
            placeholder='Username'
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
      </Form.Field>
      <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "5px", marginBottom: 5, textAlign: "left"}} >
          {(password !== confPassword ?
            <div>
            <Form.Input
              icon='key'
              type='password'
              iconPosition='left'
              error={{
                content: 'Please make sure your passwords match',
                pointing: 'below',
              }}
              placeholder='Password'
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            </div>
          :
          <div>
            <Form.Input
              icon='key'
              type='password'
              iconPosition='left'
              placeholder='Password'
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
        )}
      </Form.Field>
      <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "5px", marginBottom: 5, textAlign: "left"}} >
          <Form.Input
            icon='key'
            type='password'
            iconPosition='left'
            error={password !== confPassword}
            placeholder='Confirm Password'
            value={confPassword}
            onChange={(e) => setConfPassword(e.target.value)}
          />
      </Form.Field>
      {authContext.showErr ?
        <Message color="red" style={{marginTop:30}} >
          <Message.Header>
            Input Error
          </Message.Header>
          <div>
            {authContext.errMsg}
          </div>
        </Message>
      :<div></div>}

      {authContext.loadMsg ?
        <Message color="blue" style={{marginTop:30}} >
          <Message.Header>
            Please Do Not Refresh
          </Message.Header>
          <div>
            We are creating a blockchain account for you on Kadena
          </div>
        </Message>
      : <div></div>}
      <Button
          style={{
            backgroundColor: "#054f9e",
            color: "white",
            width: 240,
            marginTop: 10
            }}
          // onClick={() => authContext.login(uuid)}
          onClick={() => authContext.signup(ak, name, hospital, username, password)}
          loading={authContext.loading}
          disabled={!ak || !name || !hospital || !username || !password || !confPassword || confPassword !== password}
        >
        Sign Up
      </Button>
    </Form>
  )

}

export default SignUp;
