import React, { useState, useEffect, useContext, Fragment, useRef } from 'react';
import { Button, Grid, Input, Icon, Form, List, Label,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';
import QrReader from 'react-qr-reader'

const ScanTab = () => {

  const [view, setView] = useState("scan");
  const [showQR, setshowQR] = useState(false);

  return (
    <div>
      <Radio
        label={
          <label style={{color: "#18A33C", marginRight: 10, marginTop: 5, fontWeight: "bold"}}>Scan
          </label>}
        name='radioGroup'
        value='scan'
        checked={view === 'scan'}
        onChange={() => setView('scan')}
      />
      <Radio
        label={
          <label style={{color: "#18A33C", fontWeight: "bold"}}>Show
          </label>}
        name='radioGroup'
        value='show'
        checked={view === 'show'}
        onChange={() => setView('show')}
      />
      {(view === 'scan' ? <div /> : <div />)}
    </div>
  );

}

export default ScanTab;
