import React, { useState, useEffect, useContext, Fragment, useRef } from 'react';
import { Button, Grid, Input, Icon, Form, List, Label,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';
import PactCallContext from '../contexts/PactCallContext'
import InitRecord from './InitRecord';
import EndRecord from './EndRecord';
import QrReader from 'react-qr-reader'


const QRScan = () => {

  const pcContext = useContext(PactCallContext);

  const [showQR, setshowQR] = useState(true);
  const [serial, setSerial] = useState("");
  const [loading, setLoading] = useState(false);
  const [record, setRecord] = useState("");
  const [scanType, setScanType] = useState("");

  const getInfo = async (serial) => {
    setLoading(true)
    const res = await pcContext.getTest(serial);
    if (res === "failure") {
      //this public key was never registered on chain
      setScanType('fake')
    } else {
      if (res["test-init-bh"]["int"] === 0){
        //test is registered and not used yet -> SHOW INIT TEST
        setScanType('init')
      } if (res["test-init-bh"]["int"] !== 0 && res["test-init-bh"]["int"] === 0) {
        //test is registered and administered -> SHOW END TEST
        setScanType('end')
      }
    }
    setLoading(false);
  }

  const errorComponent = () => {
    return (
      <div>
        <Message color='red'>
          <Message.Header>
            Test Does Not Exist!
          </Message.Header>
            Please report immeditely
        </Message>
      </div>
    );
  }

  const handleQRScan = data => {
      if (data) {
        setSerial(data);
        setshowQR(false);
        console.log(data)
        getInfo(data);
      }
  }

  const handleQRError = err => {
    console.error(err)
  }

  return (
    <div style={{marginTop: 20}}>
    {(loading ? <Loader active inline /> :
      (scanType === "" ?
        (showQR ?
              <div>
                <QrReader
                  onError={handleQRError}
                  onScan={handleQRScan}
                />
                <Button
                    style={{
                      backgroundColor: "#18A33C",
                      color: "white",
                      width: 160,
                      marginTop: 5
                      }}
                    onClick={() => setshowQR(false)}
                  >
                  Close and Type
                </Button>
              </div>
              :
              <Form.Field  style={{width:"240px", margin: "0 auto", marginTop: "10px", marginBottom: 0}} >
                <label style={{color: "#18A33C", fontWeight: 'bold', marginBottom: 20}}>Test Public Key
                  <Popup
                    trigger={
                      <Icon name='help circle' style={{"marginLeft": "2px"}}/>
                    }
                    position='top center'
                  >
                    <Popup.Header>Test Public</Popup.Header>
                    <Popup.Content>Scan QR code on test or enter number manually</Popup.Content>
                  </Popup>
                </label>
                <div>
                  <Input
                    style={{width:"240px", marginBottom: 3, marginTop: 5}}
                    icon='hashtag'
                    iconPosition='left'
                    placeholder='Public Key'
                    value={serial}
                    onChange={(e) => setSerial(e.target.value)}
                  />
                </div>
                <Button
                    style={{
                      backgroundColor: "#18A33C",
                      color: "white",
                      width: 240,
                      marginTop: 5
                      }}
                    onClick={async () => await getInfo(serial)}

                  >
                  Submit
                </Button>
              </Form.Field>
            ) : (scanType === 'init' ? <InitRecord serial={serial} /> : (scanType === 'end' ? <EndRecord serial={serial} /> : errorComponent())))
    )}

    </div>
  )

}

export default QRScan;
