import React from 'react';
import Pact from 'pact-lang-api';
var hash = require('object-hash');
const keys = require('../config.json')

const Context = React.createContext();

const hosts = ["us1", "us2"]
const createAPIHost = (network, chainId) => `https://${network}.testnet.chainweb.com/chainweb/0.0/testnet04/chain/${chainId}/pact`

export class PactCallStore extends React.Component {

  state = {

  }

  getTest = async (pubKey) => {
    const reqKey = await Pact.fetch.local({
        networkId: "testnet04",
        pactCode:`(user.rymedi-covid.get-record ${JSON.stringify(pubKey)})`,
        keyPairs: [{...keys.rymediKeys, clist: {name: "coin.GAS", args: []}}],
        meta: Pact.lang.mkMeta("rymedi","0",0.00000001,10000,(Math.round((new Date).getTime()/1000)-15), 28800)}, createAPIHost(hosts[0], "0"))
  }

  render() {
    return (
      <Context.Provider
        value={{
          ...this.state,
        }}
      >
        {this.props.children}
      </Context.Provider>
    );
  }

}

export default Context;
