import React, { useState, useEffect, useContext } from 'react';
import { Button, Grid, Input, Icon, Form, List,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';
import AuthContext from "../contexts/AuthContext.js";


const DocInfo = () => {

  const authContext = useContext(AuthContext);

  useEffect(() => {
    async function fetchDoc() {
      await authContext.fetchDocInfo(authContext.docUuid);
    }
    fetchDoc()
  }, []);

  return (
    <div>
      <Message color="teal"  >
        <Message.Header>
          Doctor Name:
        </Message.Header>
        <div>
          {authContext.docInfo.name}
        </div>
      </Message>
      <Message color="blue"  >
        <Message.Header>
          Residency Hospital:
        </Message.Header>
        <div>
          {authContext.docInfo.hospital}
        </div>
      </Message>
      <Message color="violet"  >
        <Message.Header>
          Can Administer Tests:
        </Message.Header>
        <div>
          {(authContext.docInfo.approved === 1 ? "Yes" : "No")}
        </div>
      </Message>
      <Message color="purple"  >
        <Message.Header>
          uuid:
        </Message.Header>
        <div>
          {authContext.docInfo.uuid}
        </div>
      </Message>
      <Button
          style={{
            backgroundColor: "#054f9e",
            color: "white",
            width: 240,
            marginTop: 0
            }}
          onClick={() => authContext.logout()}
        >
        Log Out
      </Button>
    </div>
  );

}

export default DocInfo;
