import React, { useState, useEffect, useContext, Fragment, useRef } from 'react';
import { Button, Grid, Input, Icon, Form, List, Label,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';
import QrReader from 'react-qr-reader'
import DocInfo from './DocInfo';
import History from './History';
import ScanTab from './ScanTab';
import Welcome from './Welcome'

var hash = require('object-hash');


const Tabs = () => {

  const panes = [
    { menuItem: { key: 'welcome', icon: 'comment alternate', content: 'Welcome' }, render: () => <Tab.Pane>
          <div>
            <Welcome/>
          </div>
        </Tab.Pane> },
    { menuItem: { key: 'init', icon: 'qrcode', content: 'Scan' }, render: () => <Tab.Pane>
          <div>
            <Message>
              <Message.Header style={{marginBottom: 10}}>
                Scan QR Code
              </Message.Header>
                <ScanTab />
            </Message>
          </div>
        </Tab.Pane> },


  ]

  return (
    <div>
      <Tab panes={panes} menu={{color:'green', attached: 'top' }}/>
    </div>
  )

}

export default Tabs;
