import React, { useState, useEffect, useContext } from 'react';
import { Button, Grid, Input, Icon, Form, List,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';
import Login from './Login';
import Tabs from './Tabs'
import SignUp from './SignUp';
// import PactContext from "../contexts/PactContext";
import AuthContext from "../contexts/AuthContext"



const Home = () => {

  const authContext = useContext(AuthContext);

  const [view, setView] = useState("login");

  return (
    <Grid columns={1} verticalAlign="top" padded style={{backgroundColor:"black"}}>
      <Grid.Column textAlign="center" style={{overflow: "auto"}}>
          <img src={require("../assets/kadena-yellow.png")} style={{height:70, marginTop: 10}}/>
        <Header as="h6" style={{color:'white', fontWeight: 'bold', fontSize: 35, marginTop: 0}}>
          COVID-19 Testing
        </Header>
          {(authContext.logged ? <Tabs /> :
            <div>
            <Radio
              label={
                <label style={{color: "#18A33C", marginRight: 10, marginTop: 5, fontWeight: "bold"}}>Login
                </label>}
              name='radioGroup'
              value='login'
              checked={view === 'login'}
              onChange={() => setView('login')}
              />
              <Radio
              label={
                <label style={{color: "#18A33C", fontWeight: "bold"}}>Sign Up
                </label>}
                name='radioGroup'
                value='signup'
                checked={view === 'signup'}
                onChange={() => setView('signup')}
              />
              {(view === 'login' ? <Login /> : <SignUp />)}
            </div>
          )}
      </Grid.Column>

    </Grid>

  )

}

export default Home;
