import React, { useState, useEffect, useContext, Fragment, useRef } from 'react';
import { Button, Grid, Input, Icon, Form, List, Label,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';


const EndRecord = (props) => {

  return (
    <div>
      <Form.Field  style={{width:"240px", margin: "0 auto", marginTop: "10px", marginBottom: 0}} >
        <label style={{color: "#18A33C", fontWeight: 'bold',}}>Test Serial Number
          <Popup
            trigger={
              <Icon name='help circle' style={{"marginLeft": "2px"}}/>
            }
            position='top center'
          >
            <Popup.Header>Test Serial Number</Popup.Header>
            <Popup.Content>Scan QR code on test or enter number manually</Popup.Content>
          </Popup>
        </label>
        <div>
          <Input
            style={{width:"240px", marginBottom: 3, marginTop: 5}}
            icon='hashtag'
            iconPosition='left'
            placeholder='Serial Number'
            value={props.serial}
          />
        </div>
      </Form.Field>



      <Form.Field  style={{width:"240px", margin: "0 auto", marginTop: "10px", marginBottom: 0}} >
      <label style={{color: "#18A33C", fontWeight: 'bold', marginBottom: 0}}>Upload Result Image
        <Popup
          trigger={
            <Icon name='help circle' style={{"marginLeft": "2px"}}/>
          }
          position='top center'
        >
          <Popup.Header>Image</Popup.Header>
          <Popup.Content>Upload a picture of test result</Popup.Content>
        </Popup>
      </label>
      {showCam ? camera() :
        <Button
            style={{
              backgroundColor: "#18A33C",
              color: "white",
              width: 140,
              marginTop: 5
              }}
            onClick={() => setShowCam(true)}
          >
          {canClick ? "Take New" : "Take Picture"}
        </Button>
      }

      </Form.Field>


      <Form.Field  style={{width:"240px", margin: "0 auto", marginTop: "10px", marginBottom: 0}} >
      <div>
        <label style={{color: "#18A33C", fontWeight: 'bold', marginBottom: 0}}>Result
          <Popup
            trigger={
              <Icon name='help circle' style={{"marginLeft": "2px"}}/>
            }
            position='top center'
          >
            <Popup.Header>Result</Popup.Header>
            <Popup.Content>Select positive or negative test result</Popup.Content>
          </Popup>
        </label>
      </div>
        <Radio
          label={
            <label style={{color: "#18A33C", marginRight: 10}}>Positive
            </label>}
          name='rg'
          value='kp'
          checked={res === 'positive'}
          onChange={() => setRes('positive')}
          />
          <Radio
          label={
            <label style={{color: "#18A33C", marginTop: 5}}>Negative
            </label>}
            name='rg'
            value='sig'
            checked={res === 'negative'}
            onChange={() => setRes('negative')}
          />
          </Form.Field>



        <div>
          <Button
              style={{
                backgroundColor: "#18A33C",
                color: "white",
                width: 240,
                marginTop: 20
                }}
              onClick={() => pactContext.endTest(props.serial, imgHash, (res === 'positive' ? true : false), {publicKey: authContext.docInfo.public_key, secretKey: authContext.docInfo.private_key}, authContext.docInfo.uuid, imgBlob)}
              disabled={(serial === "" || res === "" || !canClick)}
              loading={pactContext.loading}
            >
            Post Test Result
          </Button>
        </div>
    </div>
  )

}

export default EndRecord

