import React, { useState, useEffect, useContext, Fragment, useRef } from 'react';
import { Button, Grid, Input, Icon, Form, List, Label,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';
import DatePicker from 'react-date-picker';
import PactContext from '../contexts/PactContext'
import AuthContext from '../contexts/AuthContext'
import PactCallContext from '../contexts/PactCallContext';


const InitRecord = (props) => {

  const pactContext = useContext(PactContext);
  const authContext = useContext(AuthContext);

  const pcContext = useContext(PactCallContext);

  const [age, setAge] = useState("");
  const [sex, setSex] = useState("");
  const [name, setName] = useState("");
  const [surname, setSurname] = useState("");
  const [dob, setDob] = useState("");
  const [id, setId] = useState("");

  const ages = [
      { key: '0', value: '0-10', text: '0-10' },
      { key: '1', value: '11-20', text: '11-20' },
      { key: '2', value: '21-30', text: '21-30' },
      { key: '3', value: '41-50', text: '41-50' },
      { key: '4', value: '51-60', text: '51-60' },
      { key: '5', value: '61-70', text: '61-70' },
      { key: '6', value: '71-80', text: '71-80' },
      { key: '7', value: '81-90', text: '81-90' },
      { key: '8', value: '91-100+', text: '91-100+' },
    ]

  return (
    <div>
      <Form.Field  style={{width:"240px", margin: "0 auto", marginTop: "10px", marginBottom: 0}} >
        <label style={{color: "#18A33C", fontWeight: 'bold', marginBottom: 20}}>Test Serial Number
          <Popup
            trigger={
              <Icon name='help circle' style={{"marginLeft": "2px"}}/>
            }
            position='top center'
          >
            <Popup.Header>Test Serial Number</Popup.Header>
            <Popup.Content>Scan QR code on test or enter number manually</Popup.Content>
          </Popup>
        </label>
        <div>
          <Input
            style={{width:"240px", marginBottom: 3, marginTop: 5}}
            icon='hashtag'
            iconPosition='left'
            placeholder='Serial Number'
            value={props.pubKey}
          />
        </div>
      </Form.Field>


      <Form.Field style={{width:"240px", margin: "0 auto", marginTop: "10px"}}>
          <label style={{color: "#18A33C", fontWeight: 'bold', marginBottom: 10}}>Patient Name
            <Popup
              trigger={
                <Icon name='help circle' style={{"marginLeft": "2px"}}/>
              }
              position='top center'
            >
              <Popup.Header>Patient Name</Popup.Header>
              <Popup.Content>Patient First Name and Last Name</Popup.Content>
            </Popup>
          </label>
          <Input
            style={{width:"240px", marginBottom: 1, marginTop: 5}}
            icon='user'
            iconPosition='left'
            placeholder='First Name'
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <Input
            style={{width:"240px", marginBottom: 3, marginTop: 2}}
            icon='user plus'
            iconPosition='left'
            placeholder='Last Name'
            value={surname}
            onChange={(e) => setSurname(e.target.value)}
          />
      </Form.Field>




    <Form.Field style={{width:"240px", margin: "0 auto", marginTop: "10px"}}>
        <label style={{color: "#18A33C", fontWeight: 'bold', marginBottom: 10}}>Patient Date of Birth
          <Popup
            trigger={
              <Icon name='help circle' style={{"marginLeft": "2px"}}/>
            }
            position='top center'
          >
            <Popup.Header>Date of Birth</Popup.Header>
            <Popup.Content>Patient Date of Birth from identification</Popup.Content>
          </Popup>
        </label>
        <DatePicker
          onChange={(date) => setDob(date)}
          value={dob}
        />
    </Form.Field>



      <Form.Field style={{width:"240px", margin: "0 auto", marginTop: "10px"}}>
        <label style={{color: "#18A33C", fontWeight: 'bold', marginBottom: 10}}>Age Group
          <Popup
            trigger={
              <Icon name='help circle' style={{"marginLeft": "2px"}}/>
            }
            position='top center'
          >
            <Popup.Header>Age Group</Popup.Header>
            <Popup.Content>Select the age range of the patient</Popup.Content>
          </Popup>
        </label>
        <Select
          style={{width:"240px", marginTop: 5}}
          placeholder='Age Group'
          options={ages}
          onChange={(e, { value }) => setAge(value)}
        />
    </Form.Field>


      <Form.Field  style={{width:"240px", margin: "0 auto", marginTop: "10px", marginBottom: 0}} >
      <div>
      <label style={{color: "#18A33C", fontWeight: 'bold', marginBottom: 10}}>Gender
        <Popup
          trigger={
            <Icon name='help circle' style={{"marginLeft": "2px"}}/>
          }
          position='top center'
        >
          <Popup.Header>Gender</Popup.Header>
          <Popup.Content>Select Gender of patient</Popup.Content>
        </Popup>
      </label>
      </div>
        <Radio
          label={
            <label style={{color: "#18A33C", marginRight: 10, marginTop: 5}}>Male
            </label>}
          name='rg'
          value='kp'
          checked={sex === 'male'}
          onChange={() => setSex('male')}
          />
          <Radio
          label={
            <label style={{color: "#18A33C", marginRight: 10}}>Female
            </label>}
            name='rg'
            value='sig'
            checked={sex === 'female'}
            onChange={() => setSex('female')}
          />
          <Radio
          label={
            <label style={{color: "#18A33C"}}>Other
            </label>}
            name='rg'
            value='sig'
            checked={sex === 'other'}
            onChange={() => setSex('other')}
          />
          </Form.Field>
          <div>
            <Button
                style={{
                  backgroundColor: "#18A33C",
                  color: "white",
                  width: 240,
                  marginTop: 20
                  }}
                onClick={() => pactContext.initTest(age, sex, props.pubKey, {publicKey: authContext.docInfo.public_key, secretKey: authContext.docInfo.private_key}, authContext.docInfo.uuid)}
                disabled={(sex === "" || age === "")}
                loading={pcContext.loading}
              >
              Administer Test
            </Button>
          </div>
    </div>
  )

}

export default InitRecord
