import React, { useState, useEffect, useContext } from 'react';
import { Button, Grid, Input, Icon, Form, List,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';

import AuthContext from "../contexts/AuthContext"


const Login = () => {

  const authContext = useContext(AuthContext);

  // const [uuid, setUuid] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  return (
    <Form>
      <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "20px", marginBottom: 0, textAlign: "left"}} >
        <label style={{color: "#054f9e" }}>Enter Your Username and Password
          <Popup
            trigger={
              <Icon name='help circle' style={{"marginLeft": "2px"}}/>
            }
            position='top center'
          >
          <Popup.Header>What is Username and Password? </Popup.Header>
          <Popup.Content>You set up a username and password when you first signed up. If you forgot them please email support@rymedi.com</Popup.Content>
          </Popup>
        </label>
          <Form.Input
            icon='md user'
            iconPosition='left'
            placeholder='Username'
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
      </Form.Field>
      <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "5px", marginBottom: 5, textAlign: "left"}} >
          <Form.Input
            icon='key'
            type='password'
            iconPosition='left'
            placeholder='Password'
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
      </Form.Field>
      <Button
          style={{
            backgroundColor: "#054f9e",
            color: "white",
            width: 240,
            marginTop: 10
            }}
          onClick={() => authContext.login(username, password)}
          // onClick={() => authContext.setLogged(true)}
          loading={authContext.loading}
          disabled={!username || !password}
        >
        Log In
      </Button>
    </Form>
  )

}

export default Login;


